import org.apache.poi.common.usermodel.HyperlinkType;import org.apache.poi.ss.usermodel.*;import java.io.*;import java.util.Arrays;import java.util.List;import java.util.Scanner;public class Main {    public static void main(String[] args) throws Exception {        Cell cell1, cell2;        for (int i = 0; i < numOfCels; i++) {            cell1 = workbook.getSheetAt(0).getRow(i).getCell(1);            cell2 = workbook.getSheetAt(0).getRow(i).getCell(0);            Main.addLink(cell1, cell2, path);        }        workbook.close();    }    private static String matcher(List<File> files, String id) throws IOException {        for (File f: files             ) {            String name = f.getName();            if (name.indexOf(id)!=-1){                return f.getCanonicalPath();            }        }        return "";    }}