import javafx.application.Application;import javafx.fxml.FXMLLoader;import javafx.scene.Group;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.input.MouseEvent;import javafx.scene.layout.AnchorPane;import javafx.scene.layout.BorderPane;import javafx.stage.Stage;import org.apache.poi.common.usermodel.HyperlinkType;import org.apache.poi.ss.usermodel.*;import java.awt.*;import java.io.*;import java.util.Arrays;import java.util.List;import java.util.Scanner;public class Main extends Application {    private Stage primaryStage;    private AnchorPane rootLayout;    public static void main(String[] args) throws Exception {        launch(args);    }    @Override    public void start(Stage primaryStage) throws IOException {        try {            AnchorPane loginNode = (AnchorPane)FXMLLoader.load(Main.class.getResource("RootLayout.fxml"));            Scene loginScene = new Scene(loginNode);            primaryStage.setTitle("Excel Filler");            primaryStage.setScene(loginScene);            primaryStage.show();        } catch (Exception ex){            System.out.println("Trouble viewing fxml file:"+ex);            System.exit(-1);        }    }    }