import javafx.application.Application;import javafx.fxml.FXMLLoader;import javafx.scene.Scene;import javafx.scene.layout.AnchorPane;import javafx.stage.Stage;import java.io.*;public class Main extends Application {    public static void main(String[] args) throws Exception {        launch(args);    }    @Override    public void start(Stage primaryStage) throws IOException {        try {            AnchorPane rootNode = (AnchorPane) FXMLLoader.load(Main.class.getResource("RootLayout.fxml"));            Scene rootScene = new Scene(rootNode);            primaryStage.setTitle("Excel Model.Filler");            primaryStage.setScene(rootScene);            primaryStage.show();        } catch (Exception ex) {            System.out.println("Trouble viewing fxml file:" + ex);            System.exit(-1);        }    }}